import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      setError(null);

      const response = await fetch('http://localhost:5000/api/students');

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        setStudents(result.data);
      } else {
        throw new Error(result.message || 'Failed to fetch students');
      }
    } catch (err) {
      console.error('Error fetching students:', err);
      setError(err.message || 'Failed to load students. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  const handleRetry = () => {
    fetchStudents();
  };

  const getGpaColor = (gpa) => {
    if (gpa >= 3.7) return '#4CAF50'; // Green
    if (gpa >= 3.0) return '#FF9800'; // Orange
    return '#F44336'; // Red
  };

  if (loading) {
    return (
      <div className="app">
        <header className="app-header">
          <h1>Student Directory</h1>
        </header>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p className="loading-text">Loading students...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <header className="app-header">
          <h1>Student Directory</h1>
        </header>
        <div className="error-container">
          <div className="error-icon">⚠️</div>
          <h3 className="error-title">Oops! Something went wrong</h3>
          <p className="error-message">{error}</p>
          <button className="retry-btn" onClick={handleRetry}>
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>Student Directory</h1>
        <p className="subtitle">Total Students: {students.length}</p>
      </header>

      <main className="main-content">
        <div className="students-grid">
          {students.map(student => (
            <div key={student.id} className="student-card">
              <div className="student-avatar">
                <img 
                  src={student.avatar} 
                  alt={`${student.name}'s avatar`}
                  onError={(e) => {
                    e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(student.name)}&size=150&background=random`;
                  }}
                />
              </div>

              <div className="student-info">
                <h3 className="student-name">{student.name}</h3>
                <p className="student-email">{student.email}</p>

                <div className="student-details">
                  <div className="detail-item">
                    <span className="label">Major:</span>
                    <span className="value">{student.major}</span>
                  </div>

                  <div className="detail-item">
                    <span className="label">Year:</span>
                    <span className="value">{student.year}</span>
                  </div>

                  <div className="detail-item">
                    <span className="label">GPA:</span>
                    <span 
                      className="value gpa" 
                      style={{ color: getGpaColor(student.gpa) }}
                    >
                      {student.gpa.toFixed(1)}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {students.length === 0 && (
          <div className="no-students">
            <p>No students found.</p>
          </div>
        )}
      </main>

      <footer className="app-footer">
        <button onClick={handleRetry} className="refresh-btn">
          Refresh Data
        </button>
      </footer>
    </div>
  );
}

export default App;
