import React from 'react';

const StudentCard = ({ student }) => {
  const getGpaColor = (gpa) => {
    if (gpa >= 3.7) return '#4CAF50'; // Green
    if (gpa >= 3.0) return '#FF9800'; // Orange
    return '#F44336'; // Red
  };

  return (
    <div className="student-card">
      <div className="student-avatar">
        <img 
          src={student.avatar} 
          alt={`${student.name}'s avatar`}
          onError={(e) => {
            e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(student.name)}&size=150&background=random`;
          }}
        />
      </div>
      
      <div className="student-info">
        <h3 className="student-name">{student.name}</h3>
        <p className="student-email">{student.email}</p>
        
        <div className="student-details">
          <div className="detail-item">
            <span className="label">Major:</span>
            <span className="value">{student.major}</span>
          </div>
          
          <div className="detail-item">
            <span className="label">Year:</span>
            <span className="value">{student.year}</span>
          </div>
          
          <div className="detail-item">
            <span className="label">GPA:</span>
            <span 
              className="value gpa" 
              style={{ color: getGpaColor(student.gpa) }}
            >
              {student.gpa.toFixed(1)}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default StudentCard;
